import React from 'react'
import styled from "styled-components";

const UploadLogo = ({type, placeholder}) => {
    return (
      <Container>
        <StyledUpload
          placeholder={placeholder && placeholder}
          type="file"
          required
          autocomplete="off"
        />
        <Status />
      </Container>
    );
};

const StyledUpload = styled.input`
    width: 80%;
    max-width: 400px;
    min-width: 300px;
    height: 60px;
    border: none;
    margin-top: 10px;
    background-color: #f5f5f5;
    box-shadow: 0px 14px 9px -15px rbga(0,0,0,0.25);
    border-radius: 8px;
    padding: 0 1rem;
    trasition: all 0.2s ease-in;
    justify-content: center;
`;

const Container  = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
`;

const Status = styled.div`
  height: 10px;
  width: 10px;
  background: #9d9d9d;
  border-radius: 10px;
  margin-left: 1rem;

  ${StyledUpload}:focus + & {
    background: #ffa689;
  }
  ${StyledUpload}:invalid + & {
    background: #fe2f75;
  }
  ${StyledUpload}:valid + & {
    background: #70edb9;
  }
`;

export default UploadLogo;
