import React from 'react'
import styled from "styled-components";

const Logo = ({type, placeholder}) => {
    return (
      <Container>
        <StyledUpload
          placeholder={placeholder && placeholder}
          type="file"
          required
          autocomplete="off"
        />
      </Container>
    );
};

const StyledUpload = styled.input`
    width: 80%;
    max-width: 400px;
    min-width: 300px;
    height: 40px;
    border: none;
    margin: 0.5rem 0;
    background-color: #f5f5f5;
    box-shadow: 0px 14px 9px -15px rbga(0,0,0,0.25);
    border-radius: 8px;
    padding: 0 1rem;
    trasition: all 0.2s ease-in;

    &:hover {
        transform: translateY(-3px);
    }

`;
