import React from "react";
import styled from "styled-components";

const products = [
    {
        product_name: 'McBurger',
    },
    {
        product_name: 'McFries',
    },
];

function Categ () {
    return (
        <Container>
            {/* <div className='item'>box-1</div>
            <div className='item'>box-2</div> */}
            <section className='item'> 
                <ProdGrid>
                    {products.map((product)=>{
                        const {product_img, product_name, product_price} = product;
                        return (
                            <Product product={product}></Product>
                        )
                    })}
                    </ProdGrid>
            </section>
        </Container>
    );
};

const Product = (props) => {
    const {product_img, product_name, product_price} = props.product;
    return (
        <article className='product_box'>
            <h1>{product_name}</h1>
        </article> 
    );
};

const Container = styled.div`
    max-height: 120px;
    border: 1px solid #ddd;
    display: flex;
    overflow-x: auto;
    position: absolute;
    width: 90%;

    .item{
        min-width:110px;
        heigh: 110px;
        line-height: 110px;
        text-align: center;
        background-color: #ddd;
        margin-right: 2px;
    }
`
const ProdGrid = styled.div`
    // .productlist {
    //     position: absolute;
    //     width: 70%;
    //     margin-top: 140px;
    //     //height: 100%;
    //     display: flex;
    //     margin: 5 rem auto;
    //     display: grid;
    //     gap: 2rem;


    // }
    @media screen and (min-width: 768px) {
        .productlist {
            // grid-template-columns: repeat(4, 1fr);
            grid-template-columns: repeat(auto-fit, minmax(177px, 1fr));
        }
    }
    .product_box{
        background: #fff;
        border-radius: 1rem;
        padding: 1rem 2rem;

        transition: all 0.2s ease-in;

        &:hover {
            transform: translateY(-4px);
        }
    }

    .product_box h1{
        margin-top: 0.5rem;
    }
    .product_box h2{
        color: #617d98;
        font-size: 0.9rem;
        margin-top: 0.25;
    }

    .image{
        height: 150px;
        width: 150px;
        resizeMode: 'contain'
    }

`;

export default Categ;
