import React, { Component } from "react";
import styled from "styled-components";
import ViewMenu from "./ViewMenu";

class ProdDesc extends Component {
    // constructor(props) {
    //     super(props);
    //     this.state = {  }
    // }
    render() { 
        return ( 
            <Container>
                <img src={this.props.product_img}/>
                <h1>{this.props.product_name}</h1>
                <h2>{this.props.product_price}</h2>
                <h2>{this.props.availability ? "Available" : "Not Available"}</h2>
            </Container>
         );
    }
}


const Container = styled.div`
    height: 500px;
    width: 20%;
    display: flex;
    flex-direction: column;
    align-items: center;
    position: fixed;
    background: #fff;
    right: 0;
    margin-top: 50px;
    border-radius: 1rem;
    margin-right: 40px;

    img{
        height: 150px;
        width: 150px;
        margin-top: 0px;
    }
`;

export default ProdDesc;
