import React from 'react'
import styled from "styled-components";

const UploadLogo = ({type, placeholder}) => {
    return (
      <Container>
        <StyledUpload
          placeholder={placeholder && placeholder}
          type="file"
          required
          autocomplete="off"
        />
        <Status />
      </Container>
    );
};

// const StyledUpload = styled.input`
//     // padding-top: 5px;
// `;

const Container  = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
`;

const Status = styled.div`
  height: 10px;
  width: 10px;
  background: #9d9d9d;
  border-radius: 10px;
  margin-left: 5.7rem;

  ${StyledUpload}:focus + & {
    background: #ffa689;
  }
  ${StyledUpload}:invalid + & {
    background: #fe2f75;
  }
  ${StyledUpload}:valid + & {
    background: #70edb9;
  }
`;

export default UploadLogo;
