import React, { Component } from "react";
import styled from "styled-components";

const products = [
    {
        product_name: 'Mains',
        id: 1
    },
    {
        product_name: 'Appetizers',
        id: 2
    },
    {
        product_name: 'Sides',
        id: 3
    },
    {
        product_name: 'Desserts',
        id: 4
    },
      {
        product_name: 'Drinks',
        id: 5
    },
];

class Categ extends Component {
    constructor(){
        super();
        this.state = { // initial/default state is the first category on the list
            clicked: false,
            current: 0
        }
        this.changeColor = this.changeColor.bind(this);
    }
    changeColor(id){
        if(this.state.current !== id) // different category is clicked
            this.setState({
                current: id,
                clicked: true
            })
    }

    render() { 
        const Product = (props) => {
            const {product_name} = props.product;
            return (
                <div className="word">
                <h1>{product_name}</h1>
                </div>
            );
        };
        return ( 
            <Container>
                {products.map((product)=>{
                    return(
                        <div
                            className={(this.state.clicked && (this.state.current===product.id)) ? 'clicked' : 'unclicked'}
                            onClick={()=>this.changeColor(product.id)}>
                            <Product product={product}></Product>
                        </div>
                    )
                })}      
            </Container>
         );
    }
}

const Container = styled.div`
    max-height: 120px;
    //border: 1px solid #ddd;
    display: flex;
    overflow-x: auto;
    position: absolute;
    width: 100%;
    margin-top: 10px;
    margin-left: 20px;


    .unclicked{
        margin-top: 10px;
        min-width: 110px;
        height: 50px;
        line-height: 70px;
        text-align: center;
        background: #FFFFFF;
        margin-right: 10px;
        border-radius: 1rem;
        transition: all 0.1s ease-in;
        /* box-shadow: 0px 0px 10px 2px #858585; */

        &:hover {
            transform: translateY(-4px);
        }
    }

    .clicked{
        margin-top: 10px;
        min-width: 110px;
        height: 50px;
        line-height: 70px;
        text-align: center;
        background: #F9C91E;
        margin-right: 10px;
        border-radius: 1rem;
        transition: all 0.1s ease-in;
        /* box-shadow: 0px 0px 10px 2px #858585; */

        &:hover {
            transform: translateY(-4px);
        }
    }

    .word{
        font-size: 0.7rem;
        margin: 0.1px 10px 0.1px 10px;
        color: black;

    }

`

export default Categ;
