import React, { Component } from "react";
import styled from "styled-components";
import burger_img from "../assets/burger.png";
import fries_img from "../assets/fries.png";
import {Link} from 'react-router-dom';
import { BiArrowBack } from "react-icons/bi";
import Categ from "./Categ";
import ProdDesc from "./ProdDesc";

const products = [
    {
        product_img: burger_img,
        product_name: 'McBurger',
        product_price: 'Php 200.00',
        id: 1
    },
    {
        product_img: fries_img,
        product_name: 'McFries',
        product_price: 'Php 50.00',
        id: 2
    },
    {
        product_img: burger_img,
        product_name: 'McNugget',
        product_price: 'Php 100.00',
        id: 3
    },
    {
        product_img: fries_img,
        product_name: 'McBurger',
        product_price: 'Php 200.00',
        id: 4
    },
    {
        product_img: burger_img,
        product_name: 'McFries',
        product_price: 'Php 50.00',
        id: 5
    },
    {
        product_img: fries_img,
        product_name: 'McNugget',
        product_price: 'Php 100.00',
        id: 6
    },
    {
        product_img: burger_img,
        product_name: 'McNugget',
        product_price: 'Php 100.00',
        id: 7
    },
    {
        product_img: fries_img,
        product_name: 'McBurger',
        product_price: 'Php 200.00',
        id: 8
    },
    {
        product_img: burger_img,
        product_name: 'McFries',
        product_price: 'Php 50.00',
        id: 9
    },
    {
        product_img: fries_img,
        product_name: 'McNugget',
        product_price: 'Php 100.00',
        id: 10
    },
    {
        product_img: burger_img,
        product_name: 'McNugget',
        product_price: 'Php 100.00',
        id: 11
    },
    {
        product_img: fries_img,
        product_name: 'McBurger',
        product_price: 'Php 200.00',
        id: 12
    },
    {
        product_img: burger_img,
        product_name: 'McFries',
        product_price: 'Php 50.00',
        id: 13
    },
    {
        product_img: fries_img,
        product_name: 'McNugget',
        product_price: 'Php 100.00',
        id: 14
    },
];

class ViewMenu extends Component {
    constructor(){
        super();
        this.state = {
            clicked: false,
            current: 0
        }
        this.changeColor = this.changeColor.bind(this);
    }
    changeColor(id){
        if(this.state.current !== id) // different category is clicked
            this.setState({
                current: id,
                clicked: true
            })
        // console.log(id)
    }
    render() { 
        const Product = (props) => {
            const {product_img, product_name, product_price} = props.product;
            return (
                <article>
                    <h3><img className='image' src={product_img} alt="" /></h3>
                    <h1>{product_name}</h1>
                    <h2>{product_price}</h2>
                </article> 
            );
        };

        return ( 
            <Container>
                <Wrapper>
                    <Nav>
                        <ArrowWrapper>
                            <Link to="/sidebar">
                                <BiArrowBack size="40px" color="#676666"/>
                            </Link>
                        </ArrowWrapper>
                        <Categ/> 
                    </Nav>

                <ProdGrid>
                    <section className='productlist'> 
                        {products.map((product, index)=>{
                            // const {product_img, product_name, product_price} = product;
                            return (
                                <div
                                    onClick={()=>this.changeColor(product.id)}
                                    className={(this.state.clicked && (this.state.current===product.id)) ? 'clicked' : 'unclicked'}>
                                    <Product product={product}></Product>
                                </div>

                            )
                        })}
                    </section>
                    <ProdDesc/>
                </ProdGrid>
            </Wrapper>
            </Container>
         );
    }
}
const Nav = styled.div`
  display: flex;
  flex-direction: row;
  max-height: 120px;
  overflow-x: auto;
  position: fixed;
  width: 100%;
  margin-top: 20px;
  margin-left: 20px;
  background: #faf0e0;
`;

const ArrowWrapper = styled.div`
  margin-top: 25px;
  margin-left: 30px;
  margin-right: 20px;

`;

const Container = styled.div`
  background: #faf0e0;
  /* background: linear-gradient(63deg, rgba(255,140,140,1) 0%, rgba(250,240,224,1) 60%, rgba(113,237,184,1) 100%);
  background-size: cover; */
  position: absolute;
  width: 100%;
  height: 100%;
  display: flex;
`;

const ProdGrid = styled.div`
    .productlist {
        position: absolute;
        width: 70%;
        margin-top: 140px;
        //height: 100%;
        display: flex;
        margin-left: 30px;
        display: grid;
        gap: 2rem;

        /* @media screen and (min-width: 768px) {
            grid-template-columns: repeat(auto-fit, minmax(177px, 1fr));
        } */
    }
    @media screen and (min-width: 768px) {
        .productlist{
            grid-template-columns: repeat(auto-fit, minmax(177px, 1fr));
        }
    }
    .product_box{
        background: #fff;
        border-radius: 1rem;
        padding: 1rem 2rem;

        transition: all 0.2s ease-in;

        &:hover {
            transform: translateY(-4px);
        }

        h1{
            margin-top: 0.5rem;
        }

        h2{
            color: #617d98;
            font-size: 0.9rem;
            margin-top: 0.25;
        }
    }
    .clicked{
        background: #F9C91E;
        border-radius: 1rem;
        padding: 1rem 2rem;

        transition: all 0.2s ease-in;

        &:hover {
            transform: translateY(-4px);
        }

        h1{
            margin-top: 0.5rem;
        }
        h2{
            color: #617d98;
            font-size: 0.9rem;
            margin-top: 0.25;
        }
    }
    .unclicked{
        background: #fff;
        border-radius: 1rem;
        padding: 1rem 2rem;

        transition: all 0.2s ease-in;

        &:hover {
            transform: translateY(-4px);
        }
        h1{
            margin-top: 0.5rem;
        }

        h2{
            color: #617d98;
            font-size: 0.9rem;
            margin-top: 0.25;
        }
    }
    /* .product_box h1{
        margin-top: 0.5rem;
    }
    .product_box h2{
        color: #617d98;
        font-size: 0.9rem;
        margin-top: 0.25;
    } */

    .image{
        height: 150px;
        width: 150px;
        resizeMode: 'contain';
    }

`;

const Wrapper = styled.div`
  /* background: rgb(255,140,140);
  background: linear-gradient(63deg, rgba(255,140,140,1) 0%, rgba(250,240,224,1) 60%, rgba(113,237,184,1) 100%); */
  width: 100%;
  height: 100%;
  display: flex;
`;

export default ViewMenu;
