import React, { Component } from "react";
import styled from "styled-components";

const products = [
    {
        product_name: 'Mains',
        id: 1
    },
    {
        product_name: 'Appetizers',
        id: 2
    },
    {
        product_name: 'Sides',
        id: 3
    },
    {
        product_name: 'Desserts',
        id: 4
    },
      {
        product_name: 'Drinks',
        id: 5
    },
];

// function Categ () {
//     return (

//     );
// };

class Categ extends Component {
    constructor(){
        super();
        // this.categId;
        this.state = {
            clicked: 0
        }
        this.changeColor = this.changeColor.bind(this);
    }
    changeColor(id){
        this.setState({
            clicked: id
        })
    }

    render() { 
        const Product = (props) => {
            const {product_name} = props.product;
            return (
                <div className='word'>
                <h1>{product_name}</h1>
                </div>
            );
        };
        return ( 
            <Container>
                {products.map((product)=>{
                    // const {product_name} = product;
                    return(
                        <div
                            sclassName='item'
                            selected={this.state.clicked}
                            onClick={()=>this.changeColor(product.id)}>
                            <Product product={product}></Product>
                        </div>
                    )
                })}      
            </Container>
         );
    }
}

// const Product = (props) => {
