// };

const Container = styled.div`
    max-height: 120px;
    //border: 1px solid #ddd;
    display: flex;
    overflow-x: auto;
    position: absolute;
    width: 90%;
    margin-top: 25px;

    .item{
        margin-top: 10px;
        min-width: 110px;
        height: 70px;
        line-height: 70px;
        text-align: center;
        background: #fff;
        margin-right: 10px;
        border-radius: 1rem;
        transition: all 0.2s ease-in;

        &:hover {
            transform: translateY(-4px);
        }
    }

    .word{
        font-size: 0.7rem;
        margin: 0.1px 10px 0.1px 10px;
    }

`

export default Categ;
