import React, { Component } from "react";
import styled from "styled-components";

const products = [
    {
        product_name: 'Mains',
        id: 1
    },
    {
        product_name: 'Appetizers',
        id: 2
    },
    {
        product_name: 'Sides',
        id: 3
    },
    {
        product_name: 'Desserts',
        id: 4
    },
      {
        product_name: 'Drinks',
        id: 5
    },
];

class Categ extends Component {
    constructor(){
        super();
        this.state = { // initial/default state is the first category on the list
            current: 1, // currently selected category (default 1)
            // new: 0, // category that has just been clicked
            isNewClicked: false,
            isDefault: true,
        }
        this.changeColor = this.changeColor.bind(this);
    }
    changeColor(id){
        if(this.state.current != id) // different category is clicked
            this.setState({
                current: id,
                isNewClicked: !this.state.isNewClicked,
                isDefault: false
            })
    }

    render() { 
        const Product = (props) => {
            const {product_name} = props.product;
            return (
                <div className='word'>
                <h1>{product_name}</h1>
                </div>
            );
        };
        return ( 
            <Container clicked={this.state.isNewClicked}>
                {products.map((product)=>{
                    // const {product_name} = product;
                    return(
                        <div
                            className='item'
                            onClick={()=>this.changeColor(product.id)}>
                            <Product product={product}></Product>
                        </div>
                    )
                })}      
            </Container>
         );
    }
}

