import styled from "styled-components";

const products = [
    {
        product_name: 'McBurger',
    },
    {
        product_name: 'McFries',
    },
    {
        product_name: 'McBurger',
    },
    {
        product_name: 'McFries',
    },
      {
        product_name: 'McBurger',
    },
];

function Categ () {
    return (
        <Container>
            {products.map((product)=>{
                const {product_name} = product;

                return (<div className='item'> <Product product={product}></Product></div>)
            })}      
        </Container>
    );
};

const Product = (props) => {
    const {product_name} = props.product;
    return (
        <h1>{product_name}</h1>
    );
};

const Container = styled.div`
    max-height: 120px;
    border: 1px solid #ddd;
    display: flex;
    overflow-x: auto;
    position: absolute;
    width: 90%;

    .item{
        min-width:110px;
        heigh: 110px;
        line-height: 110px;
        text-align: center;
        background: #fff;
        margin-right: 5px;
        border-radius: 1rem;
//     padding: 1rem 2rem;
    }

`

